/* Procedure structure for procedure `Available` */

/*!50003 DROP PROCEDURE IF EXISTS  `Available` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `Available`( IN term varchar(100) )
BEGIN
SELECT * FROM (
SELECT s.slotid, CONCAT(SUBSTRING(d.DayName, 1, 3), ' ', r.RoomNo, ' ',st.Duration,' (', r.Campus, ')') AS slotname, st.Occupied  
FROM slots s, days d, vslottype st, room r WHERE d.dayid = s.dayid AND st.SlotTypeId = s.SlotTypeId 
AND r.roomid = s.roomid 
AND slotid NOT IN (
        SELECT s.slotid FROM slots s
        JOIN SCHEDULE sch
        ON s.slotid = sch.slotid
        GROUP BY slotid
)
UNION
SELECT u.slotid , CONCAT(SUBSTRING(d.DayName, 1, 3), ' ', r.RoomNo, ' ',st.Duration,' (', r.Campus, ')') AS slotname, u.ava as 'Occupied'
FROM (
    SELECT s.*, ABS(SUM(sch.occupied)-3) as 'ava'
    FROM slots s
    JOIN SCHEDULE sch ON s.slotid = sch.slotid
    GROUP BY slotid
    HAVING ABS(SUM(sch.occupied)-3) != 0
) u
JOIN days d ON d.dayid = u.dayid
JOIN room r ON r.roomid = u.roomid
JOIN vslottype st on st.slottypeid = u.slottypeid and st.occupied = u.ava
) tab
WHERE slotname LIKE CONCAT('%', term , '%')
ORDER BY slotid LIMIT 20;
END */$$
DELIMITER ;

